    real_t false_margin = pow(exp(1), log(.5)/n);
    unsigned int temp = 1;

    for (int x = 0; x < n; ++x){
        for (int y = 0; y < 16; ++y){
            if (rand() / (real_t) RAND_MAX < false_margin){
                for (int z = 0; z < y; ++z){
                    temp *= 2;
                }
                a[x] += temp;
                temp = 1;
            }
        }
    }
    unsigned int b = 0;
    for (int x = 0; x < 16; ++x){
        temp = 1;
        for (int y = 0; y < x; ++y){
            temp *= 2;
        }
        b += temp;
    }
    #pragma acc data copyin(a[0:n])
    {
        #pragma acc kernels loop reduction(&:b)
        for (int x = 0; x < n; ++x){
            b = b & a[x];
        }
    }
    unsigned int host_b = a[0];

    for (int x = 1; x < n; ++x){
        host_b = host_b & a[x];
    }
    if (b != host_b){
        err = 1;
    }

    return err;
}
#endif

