                passed_b[y] = b[y];
                passed_c[y] = c[y];
            }
            for (int y = x + 1; y < length; ++y){
                passed_a[y - 1] = a[y];
                passed_b[y - 1] = b[y];
                passed_c[y - 1] = c[y];
            }
            if (is_possible(passed_a, passed_b, passed_c, length - 1, c[x])){
                delete[] passed_a;
                delete[] passed_b;
                delete[] passed_c;
                return true;
            }
        }
    }
    delete[] passed_a;
    delete[] passed_b;
    delete[] passed_c;
    return false;
}

