    real_t false_margin = pow(exp(1), log(.5)/n);
    unsigned int temp = 1;

    for (int x = 0; x < 10 * n; ++x){
        b[x] = (unsigned int) rand() / (real_t)(RAND_MAX / 1000);
        b_copy[x] = b[x];
        for (int y = 0; y < 16; ++y){
            if (rand() / (real_t) RAND_MAX < false_margin){
                for (int z = 0; z < y; ++z){
                    temp *= 2;
                }
                a[x] += temp;
                temp = 1;
            }
        }
    }
    #pragma acc data copyin(a[0:10 * n]) copy(b[0:10 * n], c[0:10])
    {
        #pragma acc kernels loop gang private(temp)
        for (int y = 0; y < 10; ++y){
            temp = a[y * n];
            #pragma acc loop worker reduction(&:temp)
            for (int x = 1; x < n; ++x){
                temp = temp & a[y * n + x];
            }
            c[y] = temp;
            #pragma acc loop worker
            for (int x = 0; x < n; ++x){
                b[y * n + x] = b[y * n + x] + c[y];
            }
        }
    }
    unsigned int* host_c = (unsigned int *)malloc(10 * sizeof(unsigned int));
    for (int x = 0; x < 10; ++x){
        host_c[x] = a[x * n];
        for (int y = 1; y < n; ++y){
            host_c[x] = host_c[x] & a[x * n + y];
