                a[y] = rand() / (real_t)(RAND_MAX / 10);
                host_copy[x][y] = a[y];
            }
            #pragma acc set device_num(x)
            #pragma acc enter data copyin(a[0:n])
        }
        for (int x = 0; x < acc_get_num_devices(acc_get_device_type()); ++x){
            #pragma acc set device_num(x)
            #pragma acc data present(a[0:n])
            {
                #pragma acc parallel
                {
                    #pragma acc loop
                    for (int y = 0; y < n; ++y){
                        a[y] = a[y] + 1;
                    }
                }
            }
        }
        for (int x = 0; x < acc_get_num_devices(acc_get_device_type()); ++x){
            #pragma acc set device_num(x)
